<?php	$j = 0;		$return = "";	$ipaddress = getenv(REMOTE_ADDR);	$ip = (String)$ipaddress;	$partialCause = $_POST['partialCause'];	$partialCause = str_replace('&#34;','"',$partialCause);	$partialCause = str_replace("&#39;","'",$partialCause);	$partialCause = str_replace("&#44;",",",$partialCause);	$searchQuery = $_POST['searchQuery'];	$ref = $_POST['ref'];		mysql_connect("host", "username", "password") or die("Couldnt Connect");	mysql_select_db("causes") or die("Couldnt find database");	mysql_pconnect();		$views = explode(";", $_COOKIE["views"]);	$newarr = '';		$good = false;	$num = 0;	$curcause = "";	$curnum = 0;		for($q = 0; $q < count($views); $q++)	{			$short = explode(":", $views[$q]);			$num = (int)$short[1];			$code = $short[0];			if($num > $curnum)			{				$curcause = $code;				$curnum = $num;			}	}		$favq = mysql_query('SELECT * FROM causes WHERE code="'.$curcause.'" LIMIT 1');		if ($searchQuery == "false" || $partialCause == "" || !$partialCause)	{		$tempq = mysql_query('SELECT * FROM temp WHERE ip="'.$ip.'" ORDER BY RAND() LIMIT 5');	}	if ($searchQuery == "false" || $partialCause == "" || !$partialCause)	{		$cs = mysql_query("SELECT * FROM causes ORDER BY RAND() LIMIT 12");	}	else	{		$words = explode(' ', $partialCause);		$sql = "SELECT * FROM causes WHERE";		$sql2 = "SELECT * FROM temp WHERE";		$sql_end = '';		foreach($words as $word) {			$sql_end .= " AND REPLACE(name, '#', '') LIKE '%$word%'";		}		$sql_end = substr($sql_end, 4);		$sql = $sql.$sql_end;				$sql2 = $sql2.$sql_end." AND ip='$ip' LIMIT 5";		$tempq = mysql_query($sql2);		$cs = mysql_query($sql);	}	while($f = mysql_fetch_array($favq))	{				if($ref=="")		{			$return = $return.'<div class="rip"><a href="c?c='.$f['code'].'"><span class="result">'.$f['name'].'</span></a></div>';			}		else		{			$return = $return.'<div class="rip"><a href="c?c='.$f['code'].'&r='.$ref.'"><span class="result">'.$f['name'].'</span></a></div>';			}	}	while($t = mysql_fetch_array($tempq))	{						if($ref=="")		{			$return = $return.'<div class="rip"><a href="c?t='.$t['code'].'"><span class="result">'.$t['name'].'</span></a></div>';			}		else		{			$return = $return.'<div class="rip"><a href="c?t='.$t['code'].'&r='.$ref.'"><span class="result">'.$t['name'].'</span></a></div>';			}	}	while($c = mysql_fetch_array($cs))	{			if($c['name'] != "Jatna Fund")		{			if($ref=="")			{				$return = $return.'<a href="c?c='.$c['code'].'"><span class="result">'.$c['name'].'</span></a><br/>';				}			else			{				$return = $return.'<a href="c?c='.$c['code'].'&r='.$ref.'"><span class="result">'.$c['name'].'</span></a><br/>';				}		}	}	echo $return;?>