<?php	$j = 0;		$return = "";	$ipaddress = getenv(REMOTE_ADDR);	$ip = (String)$ipaddress;	$partialCause = $_POST['partialCause'];	$partialCause = str_replace('&#34;','"',$partialCause);	$partialCause = str_replace("&#39;","'",$partialCause);	$partialCause = str_replace("&#44;",",",$partialCause);	$searchQuery = $_POST['searchQuery'];	$ref = $_POST['ref'];	$store = $_POST['store'];		mysql_connect("host", "username", "password") or die("Couldnt Connect");	mysql_select_db("causes") or die("Couldnt find database");	mysql_pconnect();		$views = explode(";", $_COOKIE["views"]);	$newarr = '';		$good = false;	$num = 0;	$curcause = "";	$curnum = 0;		for($q = 0; $q < count($views); $q++)	{			$short = explode(":", $views[$q]);			$num = (int)$short[1];			$code = $short[0];			if($num > $curnum)			{				$curcause = $code;				$curnum = $num;			}	}		if($store != "")	{	$favq = mysql_query('SELECT * FROM causes WHERE code="'.$curcause.'" LIMIT 1');		if ($searchQuery == "false" || $partialCause == "" || !$partialCause)	{		$tempq = mysql_query('SELECT * FROM temp WHERE ip="'.$ip.'" ORDER BY RAND() LIMIT 5');	}	if ($searchQuery == "false" || $partialCause == "" || !$partialCause)	{		$cs = mysql_query("SELECT * FROM causes ORDER BY RAND() LIMIT 12");	}	else	{		$tempq = mysql_query("SELECT * FROM temp WHERE REPLACE(name, '#', '') LIKE '%$partialCause%' AND ip='$ip' LIMIT 5");		$cs = mysql_query("SELECT * FROM causes WHERE REPLACE(name, '#', '') LIKE '%$partialCause%' LIMIT 12");	}		while($f = mysql_fetch_array($favq))	{				if($ref=="")		{			$return = $return.'<div class="rip"><a href="r?i='.$store.'&a='.$f['code'].'" target="_blank" onclick="calc(\''.$f['code'].'\',\''.$store.'\')"><span class="result">'.$f['name'].'</span></a></div>';			}		else		{			$return = $return.'<div class="rip"><a href="r?i='.$store.'&a='.$f['code'].'&r='.$ref.'" target="_blank" onclick="calc(\''.$f['code'].'\',\''.$store.'\')"><span class="result">'.$f['name'].'</span></a></div>';			}	}	while($t = mysql_fetch_array($tempq))	{				if($ref=="")		{			$return = $return.'<div class="rip"><a href="r?i='.$store.'&t='.$t['code'].'" target="_blank" onclick="calc(\''.$t['code'].'\',\''.$store.'\')"><span class="result">'.$t['name'].'</span></a></div>';			}		else		{			$return = $return.'<div class="rip"><a href="r?i='.$store.'&t='.$t['code'].'&r='.$ref.'" target="_blank" onclick="calc(\''.$t['code'].'\',\''.$store.'\')"><span class="result">'.$t['name'].'</span></a></div>';			}	}	while($c = mysql_fetch_array($cs))	{			if($c['name'] != "Jatna Fund")		{			if($ref=="")			{				$return = $return.'<a href="r?i='.$store.'&a='.$c['code'].'" target="_blank" onclick="calc(\''.$c['code'].'\',\''.$store.'\')"><span class="result">'.$c['name'].'</span></a><br/>';				}			else			{				$return = $return.'<a href="r?i='.$store.'&a='.$c['code'].'&r='.$ref.'" target="_blank" onclick="calc(\''.$c['code'].'\',\''.$store.'\')"><span class="result">'.$c['name'].'</span></a><br/>';				}		}	}	echo $return;	}	else	{		echo '<br/><h1 id="error1">Error.</h1><h3 id="error2">Please return to your store and try again.</h3>';	}?>